        org 0000h

; -------------------------
; 1) LD r,n
; -------------------------

        ld a,11h
        ld b,22h
        ld c,33h
        ld d,44h
        ld e,55h
        ld h,66h
        ld l,77h

; -------------------------
; 2) LD r,r'
; -------------------------

        ld a,b
        ld b,c
        ld c,d
        ld d,e
        ld e,h
        ld h,l
        ld l,a

; -------------------------
; 3) LD (HL),n
; -------------------------

        ld hl,1000h
        ld (hl),99h

; -------------------------
; 4) LD r,(HL)
; -------------------------

        ld a,(hl)
        ld b,(hl)
        ld c,(hl)

; -------------------------
; 5) LD (HL),r
; -------------------------

        ld (hl),a
        ld (hl),b
        ld (hl),c

; -------------------------
; 6) LD A,(BC)/(DE)
; -------------------------

        ld bc,2000h
        ld de,3000h

        ld a,12h
        ld (bc),a
        ld (de),a

        ld a,(bc)
        ld a,(de)

; -------------------------
; 7) LD A,(nn) / (nn),A
; -------------------------

        ld a,34h
        ld (4000h),a
        ld a,(4000h)

; -------------------------
; 8) LD rr,nn
; -------------------------

        ld bc,1111h
        ld de,2222h
        ld hl,3333h
        ld sp,4444h

; -------------------------
; 9) LD (nn),HL / HL,(nn)
; -------------------------

        ld hl,5555h
        ld (5000h),hl
        ld hl,0000h
        ld hl,(5000h)

; -------------------------
; 10) LD SP,HL
; -------------------------

        ld hl,6000h
        ld sp,hl

; -------------------------
        halt